import React, {useState, useEffect} from 'react';
import './Styles/App.css';
import '@picocss/pico';
import Mainscreen from './Components/Mainscreen';
import Game from './Components/Game';

function App() {

  const [showMainscreen, setShowMainscreen] = useState(true);

  const startButtonClick = () => {
    setShowMainscreen(false);
  }

  //Dog list as length
  const randomNumber = (length) => {
    return Math.floor(Math.random() * length);
  }

  const getDogImage = () => {
    fetch("https://dog.ceo/api/breeds/list/all")
    .then(response => response.json())
    .then(data => {
      console.log(data.message.length)
      let selectedBreed = data.message[randomNumber(data.message.length)];
      let url = `https://dog.ceo/api/breed/${selectedBreed}/images`;
      fetch(url)
        .then(response => response.json())
        .then(data => {
          let dogImage = data.message;
          return `Breed: ${selectedBreed} URL: ${dogImage}`;
        });
    })
  }

  //We'll be using the theme of DOGSSS
  useEffect(() => {
    console.log(getDogImage());
  })



  return (
    <div>
      {showMainscreen ? (<Mainscreen startButtonClick={startButtonClick}/>) : <Game/>}

    </div>
  );
}

export default App;
