import React, {useEffect, useState} from "react";
import '@picocss/pico';
import '../Styles/Game.css'

function Game(){
    const [level, setLevel] = useState(1);
    const [progress, setProgress] = useState(0);

    const randomKey = (list) => {
        const keys = Object.keys(list);
        const randomIndex = Math.floor(Math.random() * keys.length);
        return keys[randomIndex];
    }

    const getDogImages = (amount) => {
        fetch("https://dog.ceo/api/breeds/list/all")
        .then(response => response.json())
        .then(data => {
            let dogArray = [];
            for(let i = 0; i < amount; i++){
                dogArray.push(randomKey(data.message));
            }
            console.log(dogArray);
            setDogs(dogArray);
        })
    }

    const [dogs, setDogs] = useState([getDogImages(level)]);
    const renderDogImages = dogs.map((dog) => <div>{dog}</div>);

    return (
        <div>
            <nav>
                <h2><u>- LEVEL {level} -</u></h2>
            </nav>

            <section>
                <div id="gameBoard">
                    <div>{renderDogImages}</div>
                </div>
            </section>

            <section className="scores">
                <h6 id="currentScore">CURRENT SCORE: 0</h6>    
                <h6 id="bestScore">BEST SCORE: 0</h6>
            </section>

            <footer>
                <h6>Progress</h6>
                <progress value={progress} max="100"></progress>
            </footer>
        </div>
    )
}

export default Game;
