import React, {useEffect, useState} from "react";
import '@picocss/pico';
import '../Styles/Game.css';
import uniqid from 'uniqid';

function Game(){
    const [level, setLevel] = useState(0);
    const [progress, setProgress] = useState(0);

    const randomKey = (list) => {
        const keys = Object.keys(list);
        const randomIndex = Math.floor(Math.random() * keys.length);
        return keys[randomIndex];
    }

    const getImageOfDog = (breed) => {
        fetch(`https://dog.ceo/api/breed/${breed}/images`)
        .then(res => res.json())
        .then(data => {
            console.log(data.message[0]);
            return data.message[0];
        })
    }

    useEffect(() => {
        setLevel(1);
        setProgress(0);
    })

    useEffect(() => {
        const getDogs = () => {
            let dogArray = [];
            fetch("https://dog.ceo/api/breeds/list/all")
            .then(response => response.json())
            .then(data => {
                for(let i = 0; i < level; i++){
                    dogArray.push(randomKey(data.message));
                }
                setDogs(dogArray);
            })
        }
        getDogs();
    }, [level]);


    const [dogs, setDogs] = useState([]);
    const renderDogImages = dogs.map((dog) =>
        <div className="dogBox" key={uniqid()}>
            <img src={getImageOfDog(dog)}></img>
            <p className="dogName"></p>{dog}
        </div>
        );

    return (
        <div>
            <nav>
                <h2><u>- LEVEL {level} -</u></h2>
            </nav>

            <section>
                <div id="gameBoard">
                    <div>{renderDogImages}</div>
                </div>
            </section>

            <section className="scores">
                <h6 id="currentScore">CURRENT SCORE: 0</h6>    
                <h6 id="bestScore">BEST SCORE: 0</h6>
            </section>

            <footer>
                <h6>Progress</h6>
                <progress value={progress} max="100"></progress>
            </footer>
        </div>
    )
}

export default Game;
